-- Dumping data for table project_tekweb.artikel: ~7 rows (approximately)
INSERT INTO `artikel` (`id`, `judul`, `konten`, `gambar`, `tgl_rilis`) VALUES
	(7, 'ggwp', '<p>gg guys!</p>\r\n', 'B9631F47-F79D-4215-9F57-1529D5476890.jpg', '2025-05-27'),
	(8, 'makan enak', 'masak sendiri!', 'B9631F47-F79D-4215-9F57-1529D5476890.jpg', '2025-05-27'),
	(9, 'Pakuan Juara Lomba Basket Internasional', '<p><strong>Pakuan Berhasil menjuarai Loba Basket</strong></p>\r\n\r\n<p><em>isi</em></p>\r\n\r\n<p>isi 2</p>\r\n', 'Magical Shiro Onii-chan.png', '2025-05-28'),
	(10, 'bisa nihhh', '<p><strong>Bisa nih sekarang mah</strong></p>\r\n\r\n<p><em>Letsgoooo!!!</em></p>\r\n', 'Fullmetal Kamen Girlfriend-min.png', '2025-05-28'),
	(11, 'UKM Badminton Unpak Berlaga di Ajang Uption 2025 Antarperguruan Tinggi', '<p><strong>UNPAK</strong>&nbsp;&mdash; Universitas Pakuan (Unpak) melalui Unit Kegiatan Mahasiswa (UKM) Badminton turut ambil bagian dalam ajang Utama Sport Competition (Uption) 2025 yang diselenggarakan oleh Universitas Widyatama, Bandung.</p>\r\n\r\n<p>Kompetisi olahraga antar perguruan tinggi tahun 2025 ini diikuti oleh mahasiswa dari berbagai kampus.</p>\r\n\r\n<p>Dalam ajang ini, UKM Badminton Unpak menurunkan tiga tim yang terdiri dari satu pasangan ganda putra dan tiga pemain tunggal putra. Berikut nama-nama peserta:</p>\r\n\r\n<p>1.<strong>&nbsp;Muhamad Okta Parianto Putra</strong><br />\r\n2.<strong>&nbsp;Adi Hymawan</strong><br />\r\n3.<strong>&nbsp;Stevano Sofyan Syaputra</strong><br />\r\n4.<strong>&nbsp;Muhammad Rizki</strong><br />\r\n5.<strong>&nbsp;Muhammad Fajar Siddiq</strong><br />\r\n6.<strong>&nbsp;Rafli Apriansyah</strong></p>\r\n\r\n<p>Partisipasi ini menjadi bukti nyata komitmen Unpak dalam mengembangkan potensi mahasiswa, khususnya di bidang olahraga.</p>\r\n\r\n<p>Lebih dari sekadar kompetisi, Uption 2025 menjadi panggung bagi para atlet muda untuk mengasah kemampuan, menjalin relasi antarkampus, serta membawa nama baik universitas di tingkat nasional.</p>\r\n\r\n<p>Wakil Rektor Bidang Kemahasiswaan Universitas Pakuan,&nbsp;<strong><a href="https://www.unpak.ac.id/profil/tentang-kami/unsur-pimpinan">Dr. Andi Chairunnas, M.Pd.</a></strong>, menyampaikan dukungannya terhadap keikutsertaan mahasiswa dalam ajang kompetitif seperti ini.</p>\r\n\r\n<p><strong><em>&ldquo;Kami sangat mendukung penuh keikutsertaan mahasiswa dalam ajang-ajang kompetitif seperti ini. Bulu tangkis adalah olahraga yang membentuk karakter disiplin dan daya juang, dan kami ingin mahasiswa Unpak tumbuh dengan semangat itu,&rdquo;</em></strong>&nbsp;ujarnya.</p>\r\n\r\n<p>Ia juga menegaskan bahwa Universitas Pakuan akan terus memberi ruang, dukungan, dan fasilitas kepada mahasiswa untuk berkembang, baik di bidang akademik maupun non-akademik.</p>\r\n', 'UKM Badminton Unpak Berlaga di Ajang Uption 2025 Antarperguruan Tinggi.webp', '2025-05-29'),
	(12, 'Pelatihan Flood Modelling Unpak-Unsil Tingkatkan Kompetensi Mahasiswa', '<p><strong>UNPAK</strong>&nbsp;&mdash; Program Studi Teknik Sipil, Fakultas Teknik Universitas Pakuan menyelenggarakan Pelatihan Flood Modelling sebagai bagian dari implementasi kerja sama dengan Jurusan&nbsp;<a href="https://sipil-ft.unpak.ac.id/" target="_blank">Teknik Sipil</a>, Fakultas Teknik Universitas Siliwangi.</p>\r\n\r\n<p>Kegiatan ini berlangsung di Laboratorium Komputer dan GIS&nbsp;<a href="https://ft.unpak.ac.id/" target="_blank">FT Unpak</a>&nbsp;dan dilaksanakan dalam durasi setara 8 jam pelajaran.</p>\r\n\r\n<p>Pelatihan ini dirancang untuk memperkuat pemahaman mahasiswa dalam teori dasar pemodelan hidrolika aliran saluran terbuka serta penerapannya dalam perencanaan saluran drainase dan pengendalian banjir.</p>\r\n\r\n<p>Peserta dibekali dengan keterampilan menggunakan perangkat lunak HEC-RAS dan SWMM, yang sangat relevan dengan mata kuliah Hidrolika dan Rekayasa Drainase.</p>\r\n\r\n<p>Acara pelatihan ini dibuka secara resmi oleh Ketua Program Studi Teknik Sipil Universitas Pakuan.&nbsp;<a href="https://ft.unpak.ac.id/profil/struktural-profil-dosen/ir-budi-arief-m-t" target="_blank"><strong>Ir. Budi Arief, MT</strong></a>, dan dihadiri oleh puluhan mahasiswa serta dosen.</p>\r\n\r\n<p>Narasumber utama dalam pelatihan ini adalah&nbsp;<strong>Ir. Pengki Irawan, S.TP, MSi</strong>, dengan pendampingan instruktur teknis&nbsp;<strong>Junaidi Setiawan, ST</strong>&nbsp;dari Universitas Siliwangi.</p>\r\n\r\n<p>Dalam sambutannya,&nbsp;<strong><a href="https://ft.unpak.ac.id/profil/struktural-profil-dosen/wahyu-gendam-prakoso-s-tp-m-si" target="_blank">Ir. Wahyu Gendam Prakoso, S.TP, MSi</a></strong>, selaku Koordinator Laboratorium Teknik Sipil dan Koordinator Kelompok Keahlian Sumberdaya Air Unpak, menegaskan pentingnya pelatihan ini sebagai upaya meningkatkan kompetensi teknis mahasiswa dalam analisis dan permodelan sistem drainase serta mitigasi banjir.</p>\r\n\r\n<p>Kolaborasi antara Universitas Pakuan (Unpak) dan Universitas Siliwangi (Unsil) ini diharapkan dapat terus berlanjut dan menjadi langkah strategis dalam pengembangan kapasitas akademik serta keterampilan praktis mahasiswa teknik sipil.</p>\r\n', 'ft-unsil-2.webp', '2025-05-29'),
	(13, 'Strategi Produksi Iklan: Ubah Brief Menjadi Buzz Bersama Praktisi Kreatif', '<p><strong>UNPAK&nbsp;</strong>&mdash; Dalam era dimana informasi membanjiri masyarakat setiap detik, tantangan utama dalam dunia periklanan bukan hanya menciptakan konten yang menarik, tetapi juga menciptakan dampak yang melekat dalam benak audiens.</p>\r\n\r\n<p>Untuk menjawab tantangan tersebut,&nbsp;<a href="https://fisib.unpak.ac.id/" target="_blank">Fakultas Ilmu Sosial dan Ilmu Budaya (FISIB)</a>&nbsp;Universitas Pakuan (Unpak) menggelar sebuah kuliah umum bertajuk&nbsp;<strong>&quot;From Brief to Buzz: Strategi Produksi Iklan Powerful dan Relevan&quot;</strong>&nbsp;dengan Topik Utama: Produksi Periklanan dalam Marketing Komunikasi. Acara tersebut bertempat di Ruang Aula Mashudi Lantai 3 Gedung FISIB.</p>\r\n\r\n<p>Acara Kuliah Umum diawali dengan adanya sambutan oleh Wakil Dekan 2 FISIB yaitu,&nbsp;<strong>Drs. Sasongko Suharto Putro, M.M.</strong>&nbsp;dan sambutan oleh Dosen&nbsp;<strong>Karina Pramita, M.Si</strong>.</p>\r\n\r\n<p>Kuliah Umum ini menghadirkan Narasumber yaitu&nbsp;<strong>Deeng Sanyoto</strong>, seorang praktisi periklanan yang telah lama berkecimpung di dunia kreatif dan produksi media promosi dan di moderatori oleh&nbsp;<strong>Abdul Hadi</strong>, dosen sekaligus pengamat media dan komunikasi yang akan memandu jalannya diskusi serta menggali berbagai pengalaman dan perspektif dari narasumber.</p>\r\n\r\n<p>Acara ini mengangkat tema strategis dalam industri komunikasi pemasaran, yaitu bagaimana proses produksi iklan dapat dirancang sedemikian rupa sejak tahap awal penyusunan brief hingga mencapai titik puncaknya menciptakan&nbsp;<em>buzz</em>&nbsp;di kalangan target audiens.</p>\r\n\r\n<p>Tema ini sangat relevan bagi mahasiswa, akademisi, serta praktisi komunikasi dan pemasaran, karena menyentuh inti dari bagaimana ide-ide kreatif dikembangkan, diformulasikan ke dalam strategi visual dan naratif, dan kemudian dieksekusi dalam bentuk iklan yang efektif.</p>\r\n\r\n<p>Dalam dunia periklanan, istilah &quot;brief&quot; merujuk pada dokumen atau arahan awal yang diberikan kepada tim kreatif dan produksi, berisi tujuan kampanye, karakteristik audiens, pesan kunci, dan harapan hasil dari klien atau brand.</p>\r\n\r\n<p>Namun, mengubah brief menjadi&nbsp;<em>buzz</em>&nbsp;sebuah respons publik yang riuh dan menguntungkan secara citra maupun komersial bukanlah proses yang sederhana.</p>\r\n\r\n<p>Dalam sesi kuliah umum ini, narasumber akan menguraikan tahapan demi tahapan dalam proses produksi iklan yang efektif yaitu, Analisis Brief memahami pesan utama yang ingin disampaikan dan membedah audiens sasaran secara mendalam.</p>\r\n\r\n<p>Pengembangan Konsep Kreatif bagaimana ide iklan dikembangkan, dimatangkan, dan dirumuskan dalam format yang dapat diterjemahkan ke dalam media visual dan audio.</p>\r\n\r\n<p>Strategi Media pemilihan saluran komunikasi yang tepat untuk menyampaikan pesan sesuai dengan perilaku dan kebiasaan konsumsi media dari audiens target.Eksekusi Produksi proses teknis mulai dari penulisan naskah, storyboard, hingga proses shooting dan pasca produksi.</p>\r\n\r\n<p>Distribusi dan Amplifikasi membangun&nbsp;<em>buzz</em>&nbsp;melalui kampanye lintas platform, kolaborasi dengan influencer, dan teknik viral marketing.</p>\r\n\r\n<p>Evaluasi Dampak pengukuran efektivitas kampanye, baik dari segi jangkauan, engagement, maupun konversi penjualan.</p>\r\n\r\n<p>Kuliah umum ini juga mengupas bagaimana periklanan di era digital memerlukan adaptasi yang berbeda dibandingkan dengan pendekatan tradisional. Audiens saat ini memiliki kontrol yang lebih besar terhadap apa yang mereka lihat dan konsumsi.</p>\r\n\r\n<p>Oleh karena itu, iklan harus dirancang dengan pendekatan yang lebih personal, interaktif, dan berbasis data. Strategi&nbsp;<strong>&quot;from brief to buzz&quot;</strong>&nbsp;di era digital tidak hanya membutuhkan kreativitas, tetapi juga pemahaman yang mendalam tentang algoritma media sosial, perilaku digital audiens, serta integrasi antara paid, owned, dan earned media.</p>\r\n\r\n<p>Kuliah umum ini menjadi momentum penting bagi seluruh peserta untuk memperdalam wawasan mengenai bagaimana ide-ide kreatif tidak hanya diciptakan, namun juga dikelola secara strategis agar mampu menghasilkan&nbsp;<em>impact</em>&nbsp;yang signifikan dalam dunia komunikasi pemasaran.</p>\r\n\r\n<p>Tidak hanya sebagai sesi pembelajaran, kuliah umum ini juga membuka ruang dialog mahasiswa dalam mempelajari teori dan praktik, antara akademisi dan pelaku industri, serta antara generasi muda dan para profesional.</p>\r\n\r\n<p>Kuliah umum ini menekankan bahwa penghargaan kreatif bukan tujuan akhir. Follower atau viral bukan segalanya. Iklan yang baik harus mampu mendongkrak bisnis secara nyata, menyentuh emosi konsumen, dan membangun loyalitas.</p>\r\n\r\n<p>Kuliah umum ini bukan sekadar sesi pembelajaran, melainkan manifestasi dari komitmen Universitas Pakuan, khususnya FISIB, dalam membentuk insan kreatif yang strategis, empatik, dan relevan di dunia nyata.</p>\r\n', 'brief-to-buzz2.webp', '2025-05-29');

